    'aid' => '279',
    'catid' => '95',
    'typeid' => '0',
    'title' => '<font style="color:;">时刻自我“观心”，念力增上</font>',
    'tag' => '',
    'username' => 'admin',
    'userid' => '1',
    'view' => '1',
    'color' => '',
    'strong' => '0',
    'toppost' => '0',
    'font' => NULL,
    'spid' => '0',
    'ip' => NULL,
    'mtitle' => '',
    'keyword' => '',
