    'catid' => '95',
    'typeid' => '0',
    'title' => '<font style="color:;">报恩、慈悲当作是自己的责任</font>',
    'tag' => '',
    'username' => 'admin',
    'userid' => '1',
    'view' => '1',
    'color' => '',
    'strong' => '0',
    'toppost' => '0',
    'font' => NULL,
    'spid' => '0',
    'ip' => NULL,
    'mtitle' => '',
    'keyword' => '',
