    'strong' => '0',
    'toppost' => '0',
    'font' => NULL,
    'spid' => '0',
    'ip' => NULL,
    'mtitle' => '',
    'keyword' => '',
    'description' => '',
    'listorder' => NULL,
    'adddate' => '2017-02-10',
