    'typeid' => '0',
    'title' => '<font style="color:;">学佛是为了学乐，为了断苦，为了断苦的因</font>',
    'tag' => '',
    'username' => 'admin',
    'userid' => '1',
    'view' => '18',
    'color' => '',
    'strong' => '0',
    'toppost' => '0',
    'font' => NULL,
