  array (
    'aid' => '2',
    'catid' => '13',
    'typeid' => '0',
    'title' => '<font style="color:;">医院网络营销</font>',
    'subtitle' => '',
    'tag' => '',
    'username' => 'cmseasy',
    'userid' => '127',
    'view' => '1',
    'color' => '',
    'strong' => '0',
    'toppost' => '0',
    'font' => '',
